function getLevelProgress(exp, min, max, length = 10) {
  if (exp < min) exp = min;
  if (exp > max) exp = max;
  let progress = Math.floor(((exp - min) / (max - min)) * length);
  progress = Math.max(0, Math.min(progress, length)); 
  let bar = 'â–ˆ'.repeat(progress) + 'â–‘'.repeat(length - progress);
  return `[${bar}]`;
}